mapper.select:
select top 1 WorkFlowName from transactionaldata.tbl_subactivity_flowchart_def
where ProjectID =#{projectID}  
and SubActivityID = #{subActivityID} and VersionNumber = #{wfVersion} and WFID=#{wfid}
and Active=1

select ISNULL(( select top 1 ISNULL(VersionNumber,0) as VersionNumber 
from transactionalData.TBL_SUBACTIVITY_FLOWCHART_DEF where ProjectID = #{projectID}
and SubActivityID = #{subActivityID} and WorkFlowName =#{wfName} order by SubActivityFlowChartDefID desc),0)as VersionNumber

select FlowChartJSON,SubActivityFlowChartDefID,WorkFlowName,
       case when MultiView = '1'  then 'Assessed,Experienced' else 'Assessed' end as Mode,IsAutoSenseEnable as enableField
from transactionalData.TBL_SUBACTIVITY_FLOWCHART_DEF	
where ProjectID = #{projectID} and SubActivityID = #{subActivityID} 
and   WorkFlowName = #{wfName} and VersionNumber = #{wfVersion} and WFID=#{wfid}

	select * from transactionalData.TBL_SUBACTIVITY_FLOWCHART_DEF 
                where projectID=#{projectID} and subactivityID=#{subActivityID}
                and workFlowName = #{wfName} and Active =1

SELECT fsd1.StepID, fsd1.StepName, fsd1.TaskId, fsd1.task, fsd1.ExecutionType, fsd1.RpaID, AH.Savings FROM ( select stepid,stepname,taskid,task,executiontype,rpaid from transactionaldata.tbl_flowchart_step_details where SubActivityFlowChartDefID = #{oldDefID} ) fsd1 left join ( select stepid,stepname,taskid,task,executiontype,rpaid from transactionaldata.tbl_flowchart_step_details where SubActivityFlowChartDefID = #{newDefID} ) fsd2 on fsd1.StepID = fsd2.StepID inner join refdata.tbl_task t ON t.taskid = fsd1.taskid left join ( select StepID,avg(Hours) Savings from ( select StepID,bd.EndDate,Hours from transactionalData.TBL_SUBACTIVITY_FLOWCHART_DEF sfd join transactionalData.TBL_FLOWCHART_STEP_DETAILS fsd on fsd.SubActivityFlowChartDefID=sfd.SubActivityFlowChartDefID and fsd.Active=1 left join transactionalData.TBL_WORK_ORDER wo on sfd.SubActivityFlowChartDefID = wo.flowchartdefid and wo.PROJECTID =sfd.ProjectID join transactionalData.TBL_WORK_ORDER_FLOWCHART_STEP_DETAILS WOFSD on WOFSD.FlowChartStepID=fsd.StepID join transactionalData.TBL_WORK_ORDER_BOOKING_DETAILS bd on wo.WOID =bd.WOID and bd.TaskID = fsd.TaskID and bd.BookingID=WOFSD.BookingID where wo.Status = 'CLOSED' and sfd.SubActivityFlowChartDefID = #{oldDefID} and StepID in ( SELECT fsd1.stepid FROM ( select stepid,taskid from transactionaldata.tbl_flowchart_step_details where SubActivityFlowChartDefID = #{oldDefID} ) fsd1 left join ( select stepid,taskid from transactionaldata.tbl_flowchart_step_details where SubActivityFlowChartDefID = #{newDefID} ) fsd2 on fsd1.StepID = fsd2.StepID inner join refdata.tbl_task t ON t.taskid = fsd1.taskid
where fsd2.StepID is null
) and hours is not null and bd.type != 'LEADTIME' and fsd.executiontype = 'Manual' group by StepID,bd.EndDate,Hours )a group by StepID
)AH on fsd1.StepID=AH.StepID where fsd2.StepID is null


select  isCalculationNeeded, Savings
from  transactionalData.TBL_FLOWCHART_STEP_DETAILS 
where  SubActivityFlowChartDefID = #{oldDefID} and StepID = #{stepID}

    	select Savings from transactionalData.TBL_FLOWCHART_STEP_DETAILS where StepType = 'ericsson.Automatic' and 
    	SubActivityFlowChartDefID = #{oldDefID}
		and StepID = #{stepID}

    	select avg(savings) as savings from transactionalData.TBL_FLOWCHART_STEP_DETAILS where StepType = 'ericsson.Automatic' and 
    	SubActivityFlowChartDefID != #{botSavingModel.oldDefID}
		and RpaID = #{botSavingModel.bOTID}

mapper.update:
declare @proficiencyid tinyInt SELECT @proficiencyid=proficiencyid FROM refdata.tbl_wf_proficiency_type WHERE proficiencyname = 'Assessed' SELECT woid into #woid FROM transactionaldata.tbl_work_order wo INNER JOIN transactionaldata.tbl_subactivity_flowchart_def sfd ON sfd.subactivityflowchartdefid = wo.flowchartdefid AND sfd.subactivityid = wo.subactivityid WHERE wo.status IN ( 'ASSIGNED', 'REOPENED','PLANNED' ) AND wo.active = 1 AND sfd.wfid = #{workFlowId} UPDATE transactionaldata.tbl_work_order SET proficiencyid = @proficiencyid, LastModifiedBy= #{loggedInSignum}, LastModifiedDate= dbo.getDateIST()
,WorkFlowVersion=#{versionNumber}
From transactionaldata.tbl_work_order wo join #woid w ON wo.woid=w.woid

