SELECT LOEMeasurementCriterionID 
FROM [transactionalData].[TBL_SUBACTIVITY_FLOWCHART_DEF] 
where subactivityflowchartdefid = #{parentWFDefID}

Declare @FlowChartStepID varchar(200) = #{stepID}
IF EXISTS ( select 1 from transactionalData.TBL_WORK_ORDER_BOOKING_DETAILS bd left join transactionalData.TBL_WORK_ORDER wo on wo.woid = bd.woid left join transactionalData.TBL_WORK_ORDER_FLOWCHART_STEP_DETAILS wofsd on wofsd.flowchartdefid = bd.flowchartdefID and wofsd.woid = wo.woid and bd.FlowChartStepID = wofsd.FlowChartStepID
where wo.Status = 'CLOSED' and bd.FlowChartDefID= #{oldDefID} and wo.Active = 1 and wofsd.FlowChartStepID = @FlowChartStepID and bd.hours is not null and bd.hours > 0 and bd.type != 'LEADTIME' and Cast (wo.ClosedOn as Date ) >= Cast( DATEADD(day, #{date}, dbo.GetDateIST()) as Date) and Cast ( wo.ClosedOn as Date ) <= Cast ( dbo.GetDateIST() as Date) Having count (distinct wo.woid) >= #{count} ) BEGIN SELECT 1 AS 'RESULT' END ELSE BEGIN SELECT 0 AS 'RESULT' END
