mapper.select:
select tps.ProjectScopeID ,tps.ScopeName ,tps.StartDate ,tps.EndDate ,tps.ProjectID ,tps.CreatedBy ,tps.CreatedDate ,tps.requestType ,tps.vendorCode ,pdu.DeliverableUnitName as deliverableUnit ,pdu.DeliverableUnitID ,av.vendorName ,tis.sourceName as source ,tis.sourceId as sourceId ,tps.externalReference ,tps.externalProjectId ,tps.ParentWorkPlanTemplateName as externalWorkplanTemplate ,count(def.SubActivityID) as isWorkFlowCreated ,isnull(b.hasDeliverablePlan,0) as hasDeliverablePlan ,b.executionPlanId as deliverablePlanId ,tps.deliverableStatus ,st.ScopeType ,m.Method ,pf.ProjectFinancials ,case when b.executionplanid is null then tps.scopename else tps.scopename+'('+convert(varchar(15),b.executionplanid )+')' end deliverable ,convert(varchar(15),tps.vendorCode ) +'/'+ av.vendorname vendor ,Count(*) OVER() totalcounts from transactionalData.TBL_PROJECTSCOPE tps left join transactionalData.TBL_PROJECTSCOPEDETAIL psd on tps.ProjectScopeID=psd.ProjectScopeID left join transactionalData.TBL_ACTIVITYSCOPE tas on psd.ProjectScopeDetailID=tas.ProjectScopeDetailID left join transactionalData.TBL_SUBACTIVITY_FLOWCHART_DEF def on tps.ProjectID=def.ProjectID and tas.SubActivityID=def.SubActivityID and def.Active = 1 left join (select rnum as hasDeliverablePlan, executionPlanId, scopeId from (Select row_number() over(partition by executionPlanId order by executionplanid desc) as rnum, executionPlanId, scopeId from transactionalData.TBL_Deliverable_Plan_Mapping where isActive = 1) a where rnum = 1) b on b.scopeId = tps.ProjectScopeID left join refData.TBL_ASPVendor as av on tps.vendorCode = av.vendorCode left join refData.TBL_Project_Deliverable_Unit as pdu on tps.deliverableUnit = pdu.DeliverableUnitID left join refData.TBL_IntegrationSources as tis on tis.sourceId = tps.source LEFT JOIN [refData].[TBL_ScopeTypeWithDeliverableUnit] as st on st.ScopeTypeID=tps.ScopeTypeID LEFT JOIN [refData].[TBL_MethodForDeliverableUnit] as m on m.MethodID=tps.MethodID LEFT JOIN [refData].[TBL_ProjectFinancialsWithDeliverableUnit] as pf on pf.ProjectFinancialsID=tps.ProjectFinancialsID
where tps.ProjectID = #{projectId} and tps.active=1
and tps.deliverableStatus in (${status})
AND case when b.executionplanid is null then tps.scopename else tps.scopename+'('+convert(varchar(15),b.executionplanid )+')' end LIKE #{column.search}
AND deliverablestatus LIKE #{column.search}
AND pdu.DeliverableUnitName LIKE #{column.search}
AND convert(nvarchar(50), startdate,111) LIKE #{column.search}
AND convert(nvarchar(50), enddate,111) LIKE #{column.search}
AND requesttype LIKE #{column.search}
AND convert(varchar(15),tps.vendorCode ) +'/'+ av.vendorname LIKE #{column.search}
AND st.ScopeType LIKE #{column.search}
AND m.Method LIKE #{column.search}
AND pf.ProjectFinancials LIKE #{column.search}
AND tis.sourceName LIKE #{column.search}
AND externalreference LIKE #{column.search}
AND tps.externalprojectid LIKE #{column.search}
AND tps.ParentWorkPlanTemplateName LIKE #{column.search}
and ${column.data} like #{column.search}
and ( deliverable like #{dataTableRequest.search} or deliverablestatus like #{dataTableRequest.search} or deliverableUnit like #{dataTableRequest.search} or convert(nvarchar(50), startdate,126) like #{dataTableRequest.search} or convert(nvarchar(50), enddate,126) like #{dataTableRequest.search} or requesttype like #{dataTableRequest.search} or vendor like #{dataTableRequest.search} or scopeType like #{dataTableRequest.search} or method like #{dataTableRequest.search} or projectFinancials like #{dataTableRequest.search} or source like #{dataTableRequest.search} or externalreference like #{dataTableRequest.search} or externalprojectid like #{dataTableRequest.search} or externalWorkplanTemplate like #{dataTableRequest.search} )
group by tps.ProjectScopeID ,tps.ScopeName ,tps.StartDate ,tps.EndDate ,tps.ProjectID ,tps.CreatedBy ,tps.CreatedDate ,tps.requestType ,tps.vendorCode ,pdu.DeliverableUnitName ,av.vendorName ,tis.sourceName ,tis.sourceId ,tps.externalReference ,tps.externalProjectId ,tps.ParentWorkPlanTemplateName ,tps.deliverableStatus ,b.hasDeliverablePlan ,b.executionPlanId ,st.ScopeType ,m.Method ,pf.ProjectFinancials ,pdu.DeliverableUnitID
order by ${dataTableRequest.order.data} ${dataTableRequest.order.sortDir}
order by tps.ProjectScopeID
OFFSET ${dataTableRequest.start} ROWS FETCH NEXT ${dataTableRequest.length} ROWS ONLY

