declare @proficiencyid tinyInt
SELECT @proficiencyid=proficiencyid FROM refdata.tbl_wf_proficiency_type WHERE proficiencyname = 'Experienced'
SELECT @proficiencyid=proficiencyid FROM refdata.tbl_wf_proficiency_type WHERE proficiencyname = 'Assessed'
Drop TABLE IF Exists #tbl_work_flow_user_proficiency select * into #tbl_work_flow_user_proficiency from (SELECT up.calculationmode, up.calculationstartdate, up.createdby, up.createdon, up.displayedmode, up.lastmodifiedby, up.lastmodifiedon, up.projectid, up.signumid, up.subactivityid, up.wfid, up.wfuserproficenctid, up.workflowname, twpt.proficiencyname, ta1.reason, emp2.employeename as employeename, emp3.employeename as lastmodifiedByEmaployeeName, ROW_NUMBER() OVER(PARTITION BY PROJECTID,WFID,SUBACTIVITYID,signumid ORDER BY up.LastModifiedOn DESC)RN FROM TRANSACTIONALDATA.TBL_WORK_FLOW_USER_PROFICIENCY up inner Join refdata.tbl_wf_proficiency_type twpt ON twpt.proficiencyid = up.displayedmode INNER JOIN refdata.TBL_Employees emp2 ON emp2.Signum=up.SignumID INNER JOIN refdata.TBL_Employees emp3 ON emp3.Signum=up.lastmodifiedby left JOIN transactionalData.TBL_proficiency_Action ta1 ON ta1.WFUserProficiencyID=up.WFUserProficenctID WHERE
signumID IN
#{signumValue}
AND
wfid IN
#{wfId}
) tbl where rn=1
AND tbl.DisplayedMode=@proficiencyid
and (wfid like #{column.search} or workflowname like #{column.search})
and (SignumID like #{column.search} or tbl.EmployeeName like #{column.search})
and calculationMode like #{column.search}
and (lastmodifiedby like #{column.search} or tbl.lastmodifiedByEmaployeeName like #{column.search})
AND tbl.ProficiencyName LIKE #{column.search}
AND tbl.reason LIKE #{column.search}
and ${column.data} like #{column.search}
and ( wfid like #{dataTableRequest.search} or workflowname like #{dataTableRequest.search} or tbl.EmployeeName like #{dataTableRequest.search} or tbl.lastmodifiedByEmaployeeName like #{dataTableRequest.search} or tbl.ProficiencyName like #{dataTableRequest.search} or calculationMode like #{dataTableRequest.search} or signumid like #{dataTableRequest.search} or tbl.reason like #{dataTableRequest.search} or tbl.lastmodifiedon like #{dataTableRequest.search} or lastmodifiedby like #{dataTableRequest.search}
)
SELECT twp.workflowname AS WorkFlowName, twp.wfid AS workFlowId, twpt.proficiencyname AS proficiencyLevel, twp.calculationMode AS triggeredBy, CONCAT(twp.signumid,' (',emp.EmployeeName,')') AS signum, CONCAT(twp.lastmodifiedby,' (',emp1.EmployeeName,')') AS modifiedBy, twp.projectid AS projectId, twp.SubActivityId AS subActivityId, twp.DisplayedMode AS displayMode , twp.WFUserProficenctID AS wfUserProficenctID, twp.LastModifiedOn AS lastModifiedOn ,ISNULL(ta.Reason,'NA') as comments, Count(*) OVER() recordsTotal
FROM #tbl_work_flow_user_proficiency twp INNER JOIN refdata.tbl_wf_proficiency_type twpt ON twpt.proficiencyID = twp.DisplayedMode INNER JOIN refdata.TBL_Employees emp ON emp.Signum=twp.SignumID INNER JOIN refdata.TBL_Employees emp1 ON emp1.Signum=twp.lastmodifiedby left JOIN transactionalData.TBL_proficiency_Action ta ON ta.WFUserProficiencyID=twp.WFUserProficenctID WHERE twp.projectid = #{projectID} and (twpt.ProficiencyName <> 'Assessed' or calculationmode <> 'Machine' )
AND
twp.signumid IN
#{signumValue}
and
twp.wfid IN
#{wfId}
AND twp.DisplayedMode=@proficiencyid
order by ${dataTableRequest.order.data} ${dataTableRequest.order.sortDir}
ORDER BY twpt.ProficiencyID DESC
OFFSET ${dataTableRequest.start} ROWS FETCH NEXT ${dataTableRequest.length} ROWS ONLY

