declare @DefProdID TINYINT ,@KPIID TINYINT,@ExpProfID TINYINT, @Signumid varchar(10) Drop table IF Exists #tbl_work_flow_user_proficiency
select * into #tbl_work_flow_user_proficiency from (SELECT *, ROW_NUMBER() OVER(PARTITION BY PROJECTID,WFID,SUBACTIVITYID,signumid ORDER BY LastModifiedOn DESC)RN FROM TRANSACTIONALDATA.TBL_WORK_FLOW_USER_PROFICIENCY WHERE signumid IN (${signum})) tbl where rn=1
Select @DefProdID = ProficiencyID from refData.tbl_WF_Proficiency_Type where ProficiencyName = 'Assessed' ; select @KPIID = KpiID from REFDATA.TBL_WF_PROFICIENCY_KPI where KpiName = 'Qualify Count' and IsActive=1 ; Select @ExpProfID = ProficiencyID from refData.tbl_WF_Proficiency_Type where ProficiencyName = 'Experienced' and IsActive = 1 ;
Drop table if exists #tempdata
select Distinct wp.projectid,wo.WOID, wo.WOName, wo.Priority,wo.Status ,s.Activity,s.SubActivity,s.subActivityId,def.Type,
CASE WHEN wo.ProficiencyID IS NOT NULL then wo.ProficiencyID WHEN WFProf.DisplayedMode <> @DefProdID then WFProf.DisplayedMode WHEN ((WFProf.DisplayedMode is null) or (WFProf.DisplayedMode =@DefProdID )) and KV.KPIValue = 0 then @ExpProfID ELSE @DefProdID END as ProficiencyID ,
case when nodes.NodeType is null or len(nodes.NodeType) < 2 then ISNULL(ne.ElementType,'') else ISNULL(nodes.NodeType,'') end NodeType, wo.woPlanId ,ISNULL(nodes.NodeNames,'') as NodeNames,wo.actualstartdate startdate,wo.actualenddate endDate,def.versionNumber ,wo.DOID,ISNULL(ne.Market,'') as Market into #tempdata from transactionalData.TBL_WORK_ORDER wo inner join transactionalData.TBL_WORK_ORDER_PLAN wp on wp.WOPlanID=wo.WOPlanID left join transactionalData.TBL_WORK_ORDER_NODES as nodes on nodes.WOID = wo.WOID left join transactionalData.TBL_NETWORK_ELEMENT ne on ne.networkelementid=nodes.networkelementid inner join refData.TBL_SUBACTIVITY s on wo.SubActivityID = s.SubActivityID inner join transactionalData.TBL_SUBACTIVITY_FLOWCHART_DEF def on def.ProjectID = wp.ProjectID and def.SubActivityID = wo.SubActivityID and wo.WorkFlowVersion=def.versionnumber and def.SubActivityFlowChartDefID=wo.flowchartdefid inner join transactionalData.TBL_PROJECTS proj on wp.ProjectID=proj.ProjectID
LEFT join #tbl_work_flow_user_proficiency WFProf ON WO.ProjectID = WFProf.ProjectID AND WO.signumID = WFProf.SignumID And def.WFID = WFProf.WFID AND wo.SubActivityID = WFProf.SubActivityID Left Join transactionaldata.TBL_SubactivityFlowChart_Kpi_Value KV On KV.ProjectID = wo.PROJECTID and KV.SubActivityFlowChartDefID = wo.flowchartdefid and KV.SubactivityID = wo.SubActivityID and KV.IsActive = 1 and KV.KpiID = @KPIID
where wp.active=1 and wo.active=1
and wo.SignumID in (${signum}) and proj.Status != 'Closed'
and ( (wo.status in ('CLOSED','COMPLETED','REJECTED') and CONVERT(DATE, wo.ActualEndDate) between #{startDate} and #{endDate})
or ( wo.status in ('DEFERRED') and CONVERT(DATE, wo.LastModifiedDate) between #{startDate} and #{endDate} ) )
Select projectid, WOID, WOName, Priority, Status, Activity, SubActivity, subActivityId, Type, ProficiencyID, NodeType = STUFF((SELECT ', ' + NodeType FROM #tempdata b WHERE b.woid = a.woid FOR XML PATH('')), 1, 2, ''), woPlanId, NodeNames = STUFF((SELECT ', ' + NodeNames FROM #tempdata b WHERE b.woid = a.woid FOR XML PATH('')), 1, 2, ''), startdate, endDate, versionNumber, DOID, Market = STUFF((SELECT ', ' + Market FROM #tempdata b WHERE b.woid = a.woid FOR XML PATH('')), 1, 2, ''), Count(*) OVER() totalcounts FROM #tempdata a
where 1=1
and (projectid like #{column.search})
and (NodeNames like #{column.search})
AND DOID LIKE #{column.search}
AND WOID LIKE #{column.search}
AND WOName LIKE #{column.search}
AND Priority LIKE #{column.search}
AND Status LIKE #{column.search}
AND convert(nvarchar(50), startdate,126) LIKE #{column.search}
AND convert(nvarchar(50), enddate,126) LIKE #{column.search}
AND Activity LIKE #{column.search}
AND SubActivity LIKE #{column.search}
and ${column.data} like #{column.search}
and ( projectid like #{dataTableRequest.search} or NodeNames like #{dataTableRequest.search} or DOID like #{dataTableRequest.search} or WOID like #{dataTableRequest.search} or WOName like #{dataTableRequest.search} or Priority like #{dataTableRequest.search} or Status like #{dataTableRequest.search} or convert(nvarchar(50), startdate,126) like #{dataTableRequest.search} or convert(nvarchar(50), enddate,126) like #{dataTableRequest.search} or Activity like #{dataTableRequest.search} or SubActivity like #{dataTableRequest.search} )
GROUP BY projectid, WOID, WOName, Priority, Status, Activity, SubActivity, subActivityId, Type, ProficiencyID, woPlanId, startdate, endDate, versionNumber, DOID
order by ${dataTableRequest.order.data} ${dataTableRequest.order.sortDir}
order by projectid desc
OFFSET ${dataTableRequest.start} ROWS FETCH NEXT ${dataTableRequest.length} ROWS ONLY

