declare @SIGNUM VARCHAR(15) =${signum}, @STARTDATE DATE=${startDate}, @ENDDATE DATE=${endDate}, @STATUS VARCHAR(15)=${status}, @PRIORITY VARCHAR(15)=${priority}, @DOSTATUS VARCHAR(15)=${doStatus}, @ProjectIDList varchar(max)=${projectIdList}
IF(@DOSTATUS = 'ALL') Begin
SELECT distinct wo.projectid , wo.woid, wo.doid , ps.scopename AS deliverablename, wo. woname, s.subactivity , wo.priority, wo. status, wo.woplanid , CONVERT(CHAR(16), wo.plannedstartdate ,20) startdate, CONVERT(CHAR(16),wo.plannedenddate ,20) enddate, p.projectname, wo.signumid as signum, wo.workflowversion, def.WorkFlowName, CASE WHEN emp.employeename IS NULL THEN '['+wo.createdby+']' ELSE emp.employeename +'['+wo.createdby+']' END AS employeename, t.technology, wo.flowchartdefid, def.wfid, wo.PriorityLastModifiedOn as priorityModifiedOn, wo.isautosenseenable , sr.srid , isnull(ne.type,'' ) nodetype , isnull(wn1.NodeNames,'') nodenames , isnull(ne.market,'') market, ne.networkElementID, Count(*) OVER() recordsTotal
FROM transactionaldata.tbl_work_order WO LEFT JOIN transactionaldata.tbl_subactivity_flowchart_def def ON wo.projectid = def.projectid AND wo.flowchartdefid = def.subactivityflowchartdefid AND wo.subactivityid = def.subactivityid
inner JOIN (select wn.WOID ,min(wn.wnid)as wnid from transactionaldata.tbl_work_order wo1
inner join transactionaldata.tbl_work_order_nodes wn on wo1.woid=wn.woid left join transactionaldata.tbl_network_Element ne1 on ne1.NetworkElementID=wn.networkelementid where wo1.projectid in (select name from dbo.splitString(@ProjectIDList, ',')) and wo1.priority=@PRIORITY AND wo1.active=1 AND wo1.status=@STATUS AND wo1.subactivityid IS NOT NULL AND Convert(date, wo1.plannedstartdate) >=@STARTDATE AND Convert(date, wo1.plannedstartdate) <=@ENDDATE
and wn.Nodenames like #{column.search}
and ne1.Market like #{column.search}
group by wn.WOID) as N ON wo.woid=n.woid
left JOIN (select wn.WOID ,min(wn.wnid)as wnid from transactionaldata.tbl_work_order wo1 inner join transactionaldata.tbl_work_order_nodes wn on wo1.woid=wn.woid where wo1.projectid in (select name from dbo.splitString(@ProjectIDList, ',')) and wo1.priority=@PRIORITY AND wo1.active=1 AND wo1.status=@STATUS AND wo1.subactivityid IS NOT NULL AND Convert(date, wo1.plannedstartdate) >=@STARTDATE AND Convert(date, wo1.plannedstartdate) <=@ENDDATE group by wn.WOID) as N ON wo.woid=n.woid
LEFT JOIN transactionaldata.tbl_service_request SR ON wo.doid = sr.doid left join transactionaldata.tbl_work_order_nodes wn1 on wn1.wnid=n.wnid and wn1.woid=n.woid left JOIN transactionaldata.tbl_network_Element ne ON ne.NetworkElementID=wn1.networkElementid INNER JOIN transactionaldata.tbl_work_order_plan WP (nolock) ON wo.woplanid = wp.woplanid and def.projectid = wp.projectid AND wp.projectid = wo.projectid INNER JOIN transactionaldata.tbl_projectscope PS (nolock) ON wp.scopeid = ps.projectscopeid LEFT JOIN refdata.tbl_project_deliverable_unit PDU (nolock) ON ps.deliverableunit = pdu.deliverableunitid INNER JOIN refdata.tbl_subactivity S (nolock) ON wo.subactivityid = s.subactivityid INNER JOIN transactionaldata.tbl_projects P (nolock) ON wo.projectid = p.projectid LEFT JOIN refdata.tbl_employees EMP (nolock) ON emp.signum = wo.createdby INNER JOIN [REFDATA].[TBL_TECHNOLOGY] T (nolock) ON s.technologyid = t.technologyid
WHERE WO.projectid in (select name from dbo.splitString(@ProjectIDList, ',')) and wo.priority=@PRIORITY AND wo.active=1 AND wo.status=@STATUS AND wo.subactivityid IS NOT NULL AND Convert(date, plannedstartdate) >=@STARTDATE AND Convert(date, plannedstartdate) <=@ENDDATE
and WO.projectid like #{column.search}
and P.projectName like #{column.search}
and WO.doid LIKE #{column.search}
and SR.srid LIKE #{column.search}
and WO.woId LIKE #{column.search}
and WO.woName LIKE #{column.search}
and PS.SCOPENAME LIKE #{column.search}
and WO.SIGNUMID LIKE #{column.search}
and (emp.employeeName LIKE #{column.search} or WO.CREATEDBY like #{column.search})
and wo.priorityLastmodifiedon LIKE #{column.search}
and s.subactivity LIKE #{column.search}
and def.workflowname LIKE #{column.search}
and t.technology LIKE #{column.search}
group by wo.projectid, wo.woid, wo.doid , WO.WOPlanID, ps.scopename , wo.woname , s.subactivity , wo.plannedstartdate , p.projectname, def.workflowname, emp.employeename ,wo.createdby , t.technology, wo.signumid , wo.priorityLastmodifiedon , wo.isautosenseenable, wo.flowchartdefid , sr.srid, ne.Market, ne.type, wn1.nodenames, wo.PlannedEndDate, ne.NetworkElementID, wo.Priority, wo.Status, wo.SubActivityID, wo.WorkFlowVersion, def.WFID
order by ${dataTableRequest.order.data} ${dataTableRequest.order.sortDir}
order by PlannedStartDate Desc
OFFSET ${dataTableRequest.start} ROWS FETCH NEXT ${dataTableRequest.length} ROWS ONLY
End Else IF(@DOSTATUS = 'INPROGRESS') Begin
SELECT distinct wo.projectid , wo.woid, wo.doid , ps.scopename AS deliverablename, wo. woname, s.subactivity , wo.priority, wo. status, wo.woplanid , CONVERT(CHAR(16), wo.plannedstartdate ,20) startdate, CONVERT(CHAR(16),wo.plannedenddate ,20) enddate, p.projectname, wo.signumid as signum, wo.workflowversion, def.WorkFlowName, CASE WHEN emp.employeename IS NULL THEN '['+wo.createdby+']' ELSE emp.employeename +'['+wo.createdby+']' END AS employeename, t.technology, wo.flowchartdefid, def.wfid,
wo.PriorityLastModifiedOn as priorityModifiedOn, wo.isautosenseenable , sr.srid , isnull(ne.type,'' ) nodetype , isnull(wn1.nodenames,'') nodenames , isnull(ne.market,'')market, ne.networkElementID, Count(*) OVER() recordsTotal
FROM transactionaldata.tbl_work_order WO inner join transactionaldata.tbl_work_order wo1 on wo1.doid=wo.doid and wo1.PROJECTID = wo.PROJECTID and wo1.status NOT IN ('ASSIGNED') and wo1.active=1 LEFT JOIN transactionaldata.tbl_subactivity_flowchart_def def ON wo.projectid = def.projectid AND wo.flowchartdefid = def.subactivityflowchartdefid AND wo.subactivityid = def.subactivityid AND wo.workflowversion=def.versionnumber
inner JOIN (select wn.WOID ,min(wn.wnid)as wnid from transactionaldata.tbl_work_order wo1
inner join transactionaldata.tbl_work_order_nodes wn on wo1.woid=wn.woid left join transactionaldata.tbl_network_Element ne1 on ne1.NetworkElementID=wn.networkelementid where wo1.projectid in (select name from dbo.splitString(@ProjectIDList, ',')) and wo1.priority=@PRIORITY AND wo1.active=1 AND wo1.status=@STATUS AND wo1.subactivityid IS NOT NULL AND Convert(date, wo1.plannedstartdate) >=@STARTDATE AND Convert(date, wo1.plannedstartdate) <=@ENDDATE
and wn.nodenames like #{column.search}
and ne1.Market like #{column.search}
group by wn.WOID) as N ON wo.woid=n.woid
left JOIN (select wn.WOID ,min(wn.wnid)as wnid from transactionaldata.tbl_work_order wo1 inner join transactionaldata.tbl_work_order_nodes wn on wo1.woid=wn.woid where wo1.projectid in (select name from dbo.splitString(@ProjectIDList, ',')) and wo1.priority=@PRIORITY AND wo1.active=1 AND wo1.status=@STATUS AND wo1.subactivityid IS NOT NULL AND Convert(date, wo1.plannedstartdate) >=@STARTDATE AND Convert(date, wo1.plannedstartdate) <=@ENDDATE group by wn.WOID) as N ON wo.woid=n.woid
LEFT JOIN transactionaldata.tbl_service_request SR ON wo.doid = sr.doid left join transactionaldata.tbl_work_order_nodes wn1 on wn1.wnid=n.wnid and wn1.woid=n.woid LEFT JOIN transactionaldata.tbl_network_Element ne ON ne.NetworkElementID=wn1.networkelementid INNER JOIN transactionaldata.tbl_work_order_plan WP (nolock) ON wo.woplanid = wp.woplanid and def.projectid = wp.projectid AND wp.projectid = wo.projectid INNER JOIN transactionaldata.tbl_projectscope PS (nolock) ON wp.scopeid = ps.projectscopeid LEFT JOIN refdata.tbl_project_deliverable_unit PDU (nolock) ON ps.deliverableunit = pdu.deliverableunitid INNER JOIN refdata.tbl_subactivity S (nolock) ON wo.subactivityid = s.subactivityid INNER JOIN transactionaldata.tbl_projects P (nolock) ON wo.projectid = p.projectid LEFT JOIN refdata.tbl_employees EMP (nolock) ON emp.signum = wo.createdby INNER JOIN [REFDATA].[TBL_TECHNOLOGY] T (nolock) ON s.technologyid = t.technologyid
WHERE WO.projectid in (select name from dbo.splitString(@ProjectIDList, ',')) and wo.priority=@PRIORITY AND wo.active=1 AND wo.status=@STATUS AND wo.subactivityid IS NOT NULL AND Convert(date, wo.plannedstartdate) >=@STARTDATE AND Convert(date, wo.plannedstartdate) <=@ENDDATE
and WO.projectid like #{column.search}
and P.projectName like #{column.search}
and WO.doid LIKE #{column.search}
and SR.srid LIKE #{column.search}
and WO.woId LIKE #{column.search}
and WO.woName LIKE #{column.search}
and PS.SCOPENAME LIKE #{column.search}
and WO.SIGNUMID LIKE #{column.search}
and (emp.employeeName LIKE #{column.search} or WO.CREATEDBY like #{column.search})
and wo.priorityLastmodifiedon LIKE #{column.search}
and s.subactivity LIKE #{column.search}
and def.workflowname LIKE #{column.search}
and t.technology LIKE #{column.search}
group by wo.projectid, wo.woid, wo.doid , WO.WOPlanID, ps.scopename , wo.woname , s.subactivity , wo.plannedstartdate , p.projectname, def.workflowname, emp.employeename ,wo.createdby , t.technology, wo.signumid , wo.priorityLastmodifiedon , wo.isautosenseenable, wo.flowchartdefid , sr.srid, ne.Market, ne.type, wn1.nodenames, wo.PlannedEndDate, ne.NetworkElementID, wo.Priority, wo.Status, wo.SubActivityID, wo.WorkFlowVersion, def.WFID
order by ${dataTableRequest.order.data} ${dataTableRequest.order.sortDir}
