declare @woID int = #{wOID};
Drop table if exists #BookingData SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED ;With WOBS(WOID,TaskID,BookingID,FlowChartDefID,FlowChartStepID,ProficiencyID,StepType,DecisionValue) as ( select WOID, WOFCStep.TaskID, MAX(BookingID) as BookingID, FlowChartDefID, FlowChartStepID, FCStep.ProficiencyID, FCStep.StepType, DecisionValue from transactionalData.TBL_WORK_ORDER_FLOWCHART_STEP_DETAILS WOFCStep Left Join transactionalData.TBL_FLOWCHART_STEP_DETAILS FCStep On WOFCStep.FlowChartDefID = FCStep.SubActivityFlowChartDefID and WOFCStep.FlowChartStepID = FCStep.StepID and FCStep.Active = 1 Where WOID =@woID group by WOID,WOFCStep.TaskID,FlowChartDefID,FlowChartStepID, FCStep.ProficiencyID, FCStep.StepType,DecisionValue )
select wobs.WOID, wobs.TaskID, wobs.BookingID, WOBS.FlowChartDefID, WOBS.FlowChartStepID, WOBS.ProficiencyID, WOBS.StepType, case when WOBS.DecisionValue=' ''''' then 'NA' else WOBS.DecisionValue end DecisionValue, bok.StartDate,bok.EndDate,bok.Hours,bok.ParentBookingDetailsID,bok.type,bok.Status,bok.SignumID, bok.Reason,bok.OutputLink,bok.parallelcount,bok.referenceid into #BookingData from WOBS left join transactionalData.TBL_WORK_ORDER_BOOKING_DETAILS BOK ON BOK.WOID =WOBS.WOID AND BOK.BookingID =WOBS.BookingID Where wobs.WOID =@woID Drop table if exists #EffortsData select ISNULL((case when SUM(bk.Hours) >= 1 then CAST(CAST(SUM(bk.Hours) as DECIMAL(10,4)) as varchar(12)) + ' hrs' when SUM(bk.Hours) * 60 >= 1 then CAST(CAST((SUM(bk.Hours) * 60) as DECIMAL(10,4)) as varchar(12)) + ' minutes' else CAST(CAST((SUM(bk.Hours) * 60 * 60) as DECIMAL(10,2)) as varchar(12)) + ' seconds' end),0) as Effort, fs.woid, fs.FlowChartStepID into #EffortsData from transactionalData.TBL_WORK_ORDER_FLOWCHART_STEP_DETAILS fs left Join transactionalData.TBL_WORK_ORDER_BOOKING_DETAILS bk on fs.BookingID = bk.BookingID inner join #BookingData BD on BD.WOID = fs.woid and bd.FlowChartStepID = fs.FlowChartStepID Where bk.Type not in ('LEADTIME') group by fs.woid,fs.FlowChartStepID ; select Effort,BD.* from #BookingData BD left join #EffortsData ED on BD.WOID = ed.WOID and bd.FlowChartStepID = ed.FlowChartStepID

