	CreatedBy, ModifiedBy ,CONVERT(DATE,CreatedOn) createdOn ,CONVERT(DATE,ModifiedOn) modifiedOn, IsActive 
	from transactionalData.TBL_DeliverableSuccessReason where DeliverableUnit=#{deliverableUnitId}

With CTE_C AS ( select ISNULL((case when SUM(Hours) >= 1 then CAST(CAST(SUM(Hours) as DECIMAL(10,4)) as varchar(12)) + ' hrs' when SUM(Hours) * 60 >= 1 then CAST(CAST((SUM(Hours) * 60) as DECIMAL(10,4)) as varchar(12)) + ' minutes' else CAST(CAST((SUM(Hours) * 60 * 60) as DECIMAL(10,2)) as varchar(12)) + ' seconds' end),0) as Effort,fs.woid,fs.FlowChartStepID from transactionalData.TBL_WORK_ORDER_FLOWCHART_STEP_DETAILS fs Inner Join transactionalData.TBL_WORK_ORDER_BOOKING_DETAILS bk on fs.BookingID = bk.BookingID where fs.FlowChartStepID=#{stepID} and fs.woid=#{wOID} and bk.Type not in ('LEADTIME') group by fs.woid,fs.FlowChartStepID )
SELECT Effort,bk.bookingID,bk.woId,bk.taskId,bk.StartDate,bk.EndDate,bk.Hours,bk.ParentBookingDetailsID,bk.Type,bk.Status,bk.SignumID, bk.Reason,bk.OutputLink,bk.parallelcount,bk.referenceid from [transactionalData].TBL_WORK_ORDER_BOOKING_DETAILS BK join transactionalData.TBL_WORK_ORDER_FLOWCHART_STEP_DETAILS fs on fs.BookingID=bk.BookingID Join CTE_C T on t.FlowChartStepID=fs.FlowChartStepID where bk.bookingid=#{bookedID}
select Effort,woid,FlowChartStepID from CTE_C

select cells.SourceID as flowChartStepId, cells.LabelText as decisionValue from transactionalData.TBL_SUBACTIVITY_FLOWCHART_DEF sfd
CROSS APPLY OPENJSON(sfd.FlowChartJSON)
WITH(
cells nvarchar(max) as json
)as FlowChart
 CROSS APPLY OPENJSON (FlowChart.cells) WITH (
SourceID  NVARCHAR(600) '$.source.id',
TargetID NVARCHAR(600) '$.target.id',
LabelText  NVARCHAR(400) '$.labels[0].attrs.text.text'
) as cells
where sfd.SubActivityFlowChartDefID=#{flowChartDefID} 
and cells.TargetID=#{flowChartStepId}
and cells.LabelText is not null

mapper.insert:
insert into [transactionalData].[TBL_WORK_ORDER_BOOKING_DETAILS]([WOID],[TaskID],[StartDate],[EndDate],[ParentBookingDetailsID],[Type],[Status],[SignumID] ,[Reason],[outputLink],[parallelcount] ,referenceId ,CreatedBy,CreatedDate,ModifiedBy ,ModifiedDate,source_id ,[hours] ) values
( #{booking.woId},#{booking.taskId},convert(varchar(25), dbo.GetDateIST(), 120),convert(varchar(25), #{booking.endDate, jdbcType = TIMESTAMP}, 120), #{booking.parentBookingDetailsId},#{booking.type},#{booking.status} ,#{booking.signumId},#{booking.reason},#{booking.outputLink},#{parallelCount},#{booking.referenceId} ,#{booking.signumId} ,dbo.GetDateIST(),#{booking.signumId},dbo.GetDateIST(),#{booking.sourceid}
,DATEDIFF ( MI , dbo.GetDateIST(),#{booking.endDate} )/60.0
,NULL
)

mapper.update:
with CTE_A as(
select FlowChartDefID,Status from transactionalData.TBL_WORK_ORDER where
	WOID=#{wOID}
)
update transactionalData.TBL_WORK_ORDER set
flowchartdefid = #{flowChartDefID}
where WOID = #{wOID} 
and (select  Status from CTE_A) ='ASSIGNED'
and (select  FlowChartDefID from CTE_A)!=#{flowChartDefID}

