      #{woID, jdbcType=INTEGER}
      )};

SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED
declare @stepID varchar(512) = #{stepID} declare @defID int = #{defID}
select distinct CAST(FL.FlowchartTargetID as varchar(50)) as NextStepID, FS.StepName as NextStepName, FS.StepType as NextStepType from transactionalData.TBL_WORK_ORDER_FLOWCHART_STEP_LINK_DETAILS FL Inner Join transactionalData.TBL_FLOWCHART_STEP_DETAILS FS on FS.StepID = FL.FlowchartTargetID and FS.SubActivityFlowChartDefID = FL.SubActivityFlowChartDefID where FL.SubActivityFlowChartDefID =@defID and FL.FlowchartSourceID = @stepID and FS.Active=1

select  distinct WorkFlowName
from transactionalData.TBL_SUBACTIVITY_FLOWCHART_DEF
where WFID=#{wfID} and ProjectID= #{projectID} and SubActivityID=#{subactivityID} and SubActivityFlowChartDefID= #{defID}

Select flowchartdefid
 from transactionalData.TBL_WORK_ORDER WITH (NOLOCK)  
 Where WOID = #{woID}

select Distinct ISNULL(ne.Market,'') as Market , case when nodes.NodeType is null or len(nodes.NodeType) < 2 then ISNULL(ne.ElementType,'') else ISNULL(nodes.NodeType,'') end NodeType, ISNULL(nodes.NodeNames,'') as NodeNames
from transactionalData.TBL_WORK_ORDER wo left join transactionalData.TBL_WORK_ORDER_NODES as nodes on nodes.WOID = wo.WOID left join transactionalData.TBL_NETWORK_ELEMENT ne on
ne.NetworkElementID=nodes.NetworkElementID where wo.woid=#{wOID}

