declare @countryCustomerGroupId int
Select @countryCustomerGroupId= CountryCustomerGroupID from transactionalData.TBL_PROJECTS p WITH (NOLOCK) inner join transactionalData.TBL_OPPORTUNITY O WITH (NOLOCK) on p.OpportunityID=O.OpportunityID and p.ProjectID = #{projectID} inner join refData.TBL_CountryCustomerMapping ccm WITH (NOLOCK) on ccm.CountryID=o.CountryID and ccm.CustomerID=o.CustomerID
Select wOPlan.woplanid , wOPlan.projectid , wOrder.woname , wOPlan.lastmodifiedby , wOPlan.type , wOPlan.startdate , wOPlan.enddate , wOrder.woid , wOrder.plannedstartdate , wOrder.plannedenddate , wOrder.actualstartdate , wOrder.actualenddate , wOrder.parentworkorderid , wOrder.status , wOrder.signumid AS WoSignumID , Upper(wOrder.createdby) AS CreatedBY , Upper(wOrder.priority) AS Priority , Upper(Datediff(hh,wOrder.plannedstartdate, wOrder.plannedenddate)) AS SlaHrs , wOrder.closedon, worder.DOID, woplan.ScopeID, worder.SubActivityID, worder.flowchartdefid, worder.WorkFlowVersion into #tempWOData FROM transactionaldata.tbl_work_order_plan (nolock) wOPlan INNER JOIN transactionaldata.tbl_work_order (nolock) wOrder ON woPlan.woplanid = wOrder.woplanid Where wOPlan.active=1 AND wOrder.active =1 AND Status in ('ASSIGNED', 'INPROGRESS', 'REOPENED','DEFERRED','ONHOLD')
and wOPlan.ProjectID = #{projectID}
and PlannedStartDate between #{startDate} and #{endDate}
and wOrder.SignumID is null
and Status = #{woStatus}
Select woplanid,projectid,woname,lastmodifiedby,type,startdate,enddate,woid,plannedstartdate,plannedenddate,actualstartdate,actualenddate,parentworkorderid,status,WoSignumID,CreatedBY,Priority,SlaHrs,closedon,DOID,ScopeID,SubActivityID,flowchartdefid,WorkFlowVersion, CONVERT(NVARCHAR(max), ad.datecreated,121)AS LastModifiedDate into #tempWOAuditData From #tempWOData td left join transactionaldata.tbl_auditgroup ag on td.woid = ag.auditPageId LEFT JOIN ( SELECT Max(datecreated) dateCreated, auditgroupid FROM transactionaldata.tbl_auditdetails WHERE commentcategory IN ('WO_PRIORITY_EDIT', 'WO_CREATE_PLAN', 'CREATE_WORK_ORDER_PLAN') GROUP BY auditgroupid ) ad ON ad.auditgroupid=ag.auditgroupid
SELECT NAME, technologyid, market, elementtype,networkelementid,sector into #ne FROM transactionaldata.tbl_network_element (nolock) where countryCustomerGroupId = @countryCustomerGroupId
SELECT td.woplanid , td.projectid , td.woname , td.lastmodifiedby , td.type , td.startdate , td.enddate , td.LastModifiedDate , CASE WHEN wONodes.nodetype IS NULL OR Len(wONodes.nodetype) < 2 THEN ISNULL(NE.elementtype,'') ELSE ISNULL(wONodes.nodetype,'') END NodeType , ISNULL(wONodes.nodenames,'') as nodenames , ISNULL(Upper(NE.market),'') AS Market, td.woid , subFlowDef.wfid , td.plannedstartdate , td.plannedenddate , td.actualstartdate , td.actualenddate , td.parentworkorderid , td.status , td.WoSignumID , td.CreatedBY , td.Priority , td.SlaHrs , da.reason , td.closedon , sub.subactivityid , Upper(sub.activity) AS Activity , Upper(sub.subactivity) AS SubActivity , sub.technologyid , Upper(tech.technology) AS Technology , subFlowDef.workflowname , ps.scopename AS deliverableName , pdu.deliverableunitname AS deliverableUnitName , td.doid INTO #temprecs FROM #tempWOAuditData td INNER JOIN refdata.[tbl_subactivity] (nolock) sub ON td.subactivityid = sub.subactivityid INNER JOIN refdata.tbl_technology (nolock) tech ON sub.technologyid = tech.technologyid and tech.active=1 INNER JOIN transactionaldata.tbl_projectscope ps ON ps.projectscopeid = td.scopeid INNER JOIN refdata.tbl_project_deliverable_unit pdu ON ps.deliverableunit = pdu.deliverableunitid LEFT JOIN transactionaldata.tbl_subactivity_flowchart_def (nolock) subFlowDef ON td.projectid = subFlowDef.projectid AND subflowdef.subactivityflowchartdefid=td.flowchartdefid AND td.subactivityid = subFlowDef.subactivityid AND subFlowDef.versionnumber= td.workflowversion AND subFlowDef.flowchartjson IS NOT NULL LEFT JOIN transactionaldata.tbl_work_order_delivery_acceptance (nolock) da ON da.woid= td.woid LEFT JOIN transactionaldata.tbl_work_order_nodes (nolock) wONodes ON wONodes.woid = td.woid LEFT JOIN #ne NE ON NE.networkelementid =wONodes.networkelementid where tech.active=1;
WITH before_limit AS ( SELECT woplanid ,projectid ,woname ,lastmodifiedby ,type ,startdate ,enddate ,LastModifiedDate ,NodeType ,nodenames ,Market,woid ,wfid ,plannedstartdate ,plannedenddate ,actualstartdate ,actualenddate ,parentworkorderid ,status ,WoSignumID ,CreatedBY , Priority ,SlaHrs ,reason ,closedon ,subactivityid ,Activity ,SubActivity ,technologyid ,Technology ,workflowname ,deliverableName ,deliverableUnitName ,doid, Dense_rank() OVER(ORDER BY t.woplanid, t.woid) AS rnk FROM ( SELECT woplanid, projectid , woname , lastmodifiedby , type , startdate , enddate , lastmodifieddate , nodetype , nodenames , market , woid , wfid , plannedstartdate , plannedenddate , actualstartdate , actualenddate , parentworkorderid , status , wosignumid , createdby , priority , slahrs , reason , closedon , subactivityid , activity , subactivity , technologyid , technology , workflowname , deliverablename , deliverableunitname , doid FROM ( SELECT Row_number() OVER (partition BY woplanid, projectid ,woname ,lastmodifiedby ,type ,startdate ,enddate ,lastmodifieddate , nodetype ,nodenames ,market ,woid ,wfid ,plannedstartdate ,plannedenddate ,actualstartdate ,actualenddate ,parentworkorderid ,status ,wosignumid ,createdby ,priority ,slahrs ,reason ,closedon ,subactivityid ,activity ,subactivity ,technologyid ,technology ,workflowname ,deliverablename ,deliverableunitname ,doid ORDER BY projectid ) AS rnum, woplanid ,projectid ,woname ,lastmodifiedby ,type ,startdate ,enddate ,LastModifiedDate ,NodeType ,nodenames ,Market,woid ,wfid ,plannedstartdate ,plannedenddate ,actualstartdate ,actualenddate ,parentworkorderid ,status ,WoSignumID ,CreatedBY , Priority ,SlaHrs ,reason ,closedon ,subactivityid ,Activity ,SubActivity ,technologyid ,Technology ,workflowname ,deliverableName ,deliverableUnitName ,doid FROM #temprecs a )b WHERE rnum = 1 ) t where 1=1
and ${column.data} like #{column.search}
and (WorkFlowName like #{dataTableReq.search} or WOID like #{dataTableReq.search} or Status like #{dataTableReq.search} or Reason like #{dataTableReq.search} or NodeNames like #{dataTableReq.search} or WoSignumID like #{dataTableReq.search} or PlannedStartDate like #{dataTableReq.search} or PlannedEndDate like #{dataTableReq.search} or deliverableName like #{dataTableReq.search} or DeliverableUnitName like #{dataTableReq.search} or UPPER(Priority) like #{dataTableReq.search} or DOID like #{dataTableReq.search}
or ${column.data} like #{dataTableReq.search}
)
)
SELECT woplanid ,projectid ,woname ,lastmodifiedby ,type ,startdate ,enddate ,LastModifiedDate ,NodeType ,nodenames ,Market,woid ,wfid ,plannedstartdate ,plannedenddate ,actualstartdate ,actualenddate ,parentworkorderid ,status ,WoSignumID ,CreatedBY , Priority ,SlaHrs ,reason ,closedon ,subactivityid ,Activity ,SubActivity ,technologyid ,Technology ,workflowname ,deliverableName ,deliverableUnitName ,doid, rnk , ( SELECT Max(before_limit.rnk) FROM before_limit ) AS totalcnt FROM before_limit
order by ${dataTableReq.order.data} ${dataTableReq.order.sortDir}
OFFSET ${dataTableReq.start} ROWS FETCH NEXT ${dataTableReq.length} ROWS ONLY

SELECT count(distinct WOPlanID) FROM ( SELECT distinct wOPlan.WOPlanID ,wOPlan.ProjectID ,wOPlan.StartDate ,wOPlan.EndDate ,wOrder.WOName ,wOPlan.CreatedBy ,wOPlan.LastModifiedBy ,convert(nvarchar(MAX), ad.dateCreated,121)as LastModifiedDate ,UPPER(wOplan.Priority) as Priority ,case when wONodes.NodeType is null or len(wONodes.NodeType) < 2 then NE.ElementType else wONodes.NodeType end NodeType , wONodes.NodeNames ,UPPER(NE.Market) as Market , wOrder.WOID ,subFlowDef.WFID , wOrder.PlannedStartDate , wOrder.PlannedEndDate , wOrder.ActualStartDate , wOrder.ActualEndDate , wOrder.parentWorkOrderID , wOrder.Status , wOrder.SignumID as WoSignumID , UPPER(wOrder.CreatedBY) AS WoCreatedBY , UPPER(wOrder.Priority) AS WoPriority , UPPER(DATEDIFF(HH,wOrder.plannedstartdate, wOrder.plannedenddate)) AS SlaHrs , da.Reason , wOrder.ClosedOn , sub.SubActivityID , UPPER(sub.Activity) as Activity , UPPER(sub.SubActivity) as SubActivity , sub.DomainID , sub.ServiceAreaID , sub.TechnologyID , CONCAT(dom.Domain,'/',dom.SubDomain) as Domain , UPPER(tech.Technology) as Technology , subFlowDef.WorkFlowName , ps.ScopeName as deliverableName , pdu.DeliverableUnitName as deliverableUnitName , doi.DOID FROM transactionalData.TBL_WORK_ORDER_PLAN (NOLOCK) wOPlan inner join transactionalData.TBL_Deliverable_Order_ID(nolock) doi on doi.WOPlanID=wOPlan.WOPlanID inner join transactionaldata.tbl_work_order (nolock) wOrder on doi.DOID = wOrder.DOID inner join refData.[TBL_SUBACTIVITY] (NOLOCK) sub on wOrder.SubActivityID = sub.SubActivityID inner join refData.TBL_DOMAIN (NOLOCK) dom on sub.DomainID = dom.DomainID inner join refData.TBL_SERVICEAREA (NOLOCK) ser on sub.ServiceAreaID = ser.ServiceAreaID inner join refData.TBL_TECHNOLOGY (NOLOCK) tech on sub.TechnologyID = tech.TechnologyID inner join transactionalData.TBL_PROJECTSCOPE ps on ps.ProjectScopeID = wOPlan.ScopeID inner join refData.TBL_Project_Deliverable_Unit pdu on ps.deliverableUnit = pdu.DeliverableUnitID left join transactionalData.TBL_WORK_ORDER_NODES (NOLOCK) wONodes on wONodes.WOID = wOrder.WOID left join transactionalData.TBL_SUBACTIVITY_FLOWCHART_DEF (NOLOCK) subFlowDef on wOPlan.ProjectID = subFlowDef.ProjectID and subflowdef.SubActivityFlowChartDefID=worder.flowchartdefid and wOrder.SubActivityID = subFlowDef.SubActivityID and subFlowDef.VersionNumber= wOrder.WorkFlowVersion and subFlowDef.flowchartJSON is not null left join transactionaldata.tbl_work_order_delivery_acceptance (NOLOCK) da on da.woID= wOrder.woID left join transactionalData.TBL_NETWORK_ELEMENT (nolock) NE on NE.ProjectID=wOPlan.ProjectID and NE.Name=wONodes.NodeNames left join transactionalData.TBL_AuditGroup ag on ag.auditPageId=wOrder.WOID left join (select max(dateCreated) dateCreated,auditgroupid from transactionalData.TBL_AuditDetails where commentCategory in ('WO_PRIORITY_EDIT','WO_CREATE_PLAN','CREATE_WORK_ORDER_PLAN') group by auditgroupid) ad on ad.auditgroupid=ag.auditgroupid where wOPlan.Active=1 and wOrder.Active =1 and dom.active=1 and ser.active=1 and tech.active=1 and Status in ('ASSIGNED', 'INPROGRESS', 'REOPENED','DEFERRED','ONHOLD')
and wOPlan.ProjectID = #{projectID}
and PlannedStartDate between #{startDate} and #{endDate}
and wOrder.SignumID is null
and Status = #{woStatus}
) t where 1=1
and ${column.data} like #{column.search}
and WoPriority like #{column.search}
and ( WorkFlowName like #{dataTableReq.search} or WOID like #{dataTableReq.search} or Status like #{dataTableReq.search} or Reason like #{dataTableReq.search} or NodeNames like #{dataTableReq.search} or WoSignumID like #{dataTableReq.search} or PlannedStartDate like #{dataTableReq.search} or PlannedEndDate like #{dataTableReq.search} or deliverableName like #{dataTableReq.search} or deliverableUnitName like #{dataTableReq.search} or WoPriority like #{dataTableReq.search} or DOID like #{dataTableReq.search}
or ${column.data} like #{dataTableReq.search}
)

