#{Item}
AND wo.active = 1

IF EXISTS(select 1 from transactionalData.TBL_Delivery_Responsible where signumID = #{lastModifiedBy} and projectID = #{projectID} and Active = 1) or exists( select 1 from transactionalData.TBL_Projects where projectCreator = #{lastModifiedBy} and projectID = #{projectID}
) or exists(
SELECT 1 FROM transactionaldata.tbl_resourcerequests RR (nolock) JOIN transactionaldata.tbl_resourceposition RP (nolock) ON RR.resourcerequestid = RP.resourcerequestid JOIN transactionaldata.tbl_workeffort WF (nolock) ON WF.resourcepositionid = RP.resourcepositionid WHERE WF.signum = #{lastModifiedBy} AND WF.enddate >= CONVERT(VARCHAR(8), dbo.Getdateist(), 1) AND WF.positionstatus = 'DEPLOYED' AND WF.isactive = 1 AND RP.isactivie = 1 and RR.ProjectID = #{projectID}
) BEGIN SELECT 1 AS 'RESULT' END ELSE BEGIN SELECT 0 AS 'RESULT' END
